mapper.select:
SELECT * FROM ( SELECT A.*, ROWNUM AS RNUM, COUNT(*) OVER() AS TOTAL FROM(
SELECT NUM, TITLE, NAME, TO_CHAR(REGDATE,'YYYY/MM/DD') REGDATE, CONTENT FROM FREEBOARD WHERE 1=1
AND NUM=#{find}
AND TITLE LIKE '%'||#{find}||'%'
AND TO_CHAR(REGDATE,'YYYYMMDD') BETWEEN #{rega} AND #{regb}
ORDER BY LPAD( NUM, 5 ) DESC ) A ) WHERE RNUM BETWEEN #{startNo} AND #{endNo}

SELECT COUNT (*) FROM FREEBOARD WHERE 1=1

			 AND NUM=#{find}


			AND TITLE LIKE '%'||#{find}||'%'


			AND TO_CHAR(REGDATE,'YYYYMMDD') BETWEEN #{rega} AND #{regb}

mapper.update:
UPDATE FREEBOARD
SET TITLE = #{title}
		, CONTENT = #{content}
WHERE NUM = #{num}
